import React, { useState } from 'react';
import axios from 'axios';
import {
  ReactionButtonWrapper,
  ReactionsWrapper,
  EventIDWrapper,
  HostNameWrapper,
  HostImageWrapper,

  EventDetailsBottomWrapper,
  EventInfoWrapper,
  EventButtonsWrapper} from './EventDetailsBottom.styles';
import { useAppState } from "../../AppStateContext"
import {useAnimation} from 'framer-motion';

import type { } from "styled-components/cssprop";
import { couldStartTrivia, createObjectBindingPattern, setSyntheticTrailingComments } from 'typescript';

interface EventDetailsBottomProps {

}

export const EventDetailsBottom: React.SFC<EventDetailsBottomProps> = () => {

  const { state } = useAppState();

  const [laugh, setLaugh] = useState(0);
  const [thumbUp, setThumbUp] = useState(0);
  const [love, setLove] = useState(0);
  const [mad, setMad] = useState(0);
  const [sad, setSad] = useState(0);
  const [opacity, setOpacity] = useState(100);
  const [beginning, setBeginning] = useState(0);
  const [sticker, setSticker] = useState<any>([]);
  const [full, setFull] = useState(0)

  const controls = useAnimation();

  const container = {
    hidden: { opacity : 0 },
    show: {
      opacity: 1
    }
  }

  const config = {
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json',
      'Access-Control-Allow-Origin': '*',
      'apiKey': 'c61fc5d0a6b7600ccb94edbca4e86a8a'
    }
  }
  React.useEffect(() => {
    fetch('/v1/package?userId=123&pageNumber=1&country=us', {
      // const response = fetch('/v1/package?userId=123&pageNumber=1&country=us', {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Access-Control-Allow-Origin': '*',
          'apiKey': 'c61fc5d0a6b7600ccb94edbca4e86a8a'
        },
        method:"get"
      })
      .then((response) => response.json())
      .then((json) => {setSticker(json.body.packageList)})      // .then((json) => setStickers(json.body.packageList))
      // .then(json => console.log(json))
        // .then((responseJson) => {console.log(responseJson)});
      // console.log(response);
  }, [])

  console.log("Sticker: ", sticker);

  const between = (min, max) => Math.floor(
      Math.random() * (max - min) + min
    )

  return (

    <EventDetailsBottomWrapper>
      <div style={{ display: 'flex'}}>
        <HostImageWrapper src={state.eventAvatar} />

        <EventInfoWrapper>
          <HostNameWrapper>{state.eventName}</HostNameWrapper>
          <EventIDWrapper>Event  ID: {state.eventId}</EventIDWrapper>
        </EventInfoWrapper>
      </div>
      <div>
      <EventButtonsWrapper>

        <ReactionsWrapper>
          <ReactionButtonWrapper onClick={() => {
            const wayUp = between(-280, -260);
            const transparent = 0;
            setFull(100);
            setOpacity(transparent);
            setLove(wayUp);
          }} src={sticker.length > 0 && sticker[0].packageImg} />

          <ReactionButtonWrapper animate={{ y: [0, laugh] }} onClick={() => {
            const wayUp = between(-280, -260);
            setLaugh(wayUp);
          }} src={sticker.length > 0 && sticker[1].packageImg} />

          <ReactionButtonWrapper animate={{ y: [0, thumbUp] }} onClick={() => {
            const wayUp = between(-280, -260);
            setThumbUp(wayUp);
          }} src={sticker.length > 0 && sticker[2].packageImg} />

          <ReactionButtonWrapper animate={{ y: [0, mad] }} onClick={() => {
            const wayUp = between(-280, -260);
            setMad(wayUp);
          }} src={sticker.length > 0 && sticker[3].packageImg} />

          <ReactionButtonWrapper animate={{ y: [0, sad] }} onClick={() => {
            const wayUp = between(-280, -260);
            setSad(wayUp);
          }} src={sticker.length > 0 && sticker[4].packageImg} />

          {/* <ReactionButtonWrapper animate={{ y: [thumbUp, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setThumbUp(wayUp);
          }} src={sticker[1].packageImg} />

          <ReactionButtonWrapper animate={{ y: [mad, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setMad(wayUp);
          }} src={sticker[2].packageImg} />

          <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setSad(wayUp)
          }} src={sticker[3].packageImg} />

          <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setSad(wayUp)
          }} src={sticker[4].packageImg} /> */}

        </ReactionsWrapper>



      </EventButtonsWrapper>
      <EventButtonsWrapper>

<ReactionsWrapper>
  <ReactionButtonWrapper 
    animate={{ y: [0, love], opacity: [0, full]}}
    initial={false}
    transition={{ ease: "easeOut", duration: 2 }}  
    src={sticker.length > 0 && sticker[0].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, laugh], opacity: [0, 100] }} 
    transition={{ ease: "easeOut", duration: 2 }}  

    src={sticker.length > 0 && sticker[1].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, thumbUp] }}
    transition={{ ease: "easeOut", duration: 2 }}  

    src={sticker.length > 0 && sticker[2].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, mad] }} 
    transition={{ ease: "easeOut", duration: 2 }}  

    src={sticker.length > 0 && sticker[3].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, sad] }}
    transition={{ ease: "easeOut", duration: 2 }}  

    src={sticker.length > 0 && sticker[4].packageImg} 
  />

  {/* <ReactionButtonWrapper animate={{ y: [thumbUp, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setThumbUp(wayUp);
  }} src={sticker[1].packageImg} />

  <ReactionButtonWrapper animate={{ y: [mad, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setMad(wayUp);
  }} src={sticker[2].packageImg} />

  <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setSad(wayUp)
  }} src={sticker[3].packageImg} />

  <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setSad(wayUp)
  }} src={sticker[4].packageImg} /> */}

</ReactionsWrapper>



</EventButtonsWrapper>
      </div>
    </EventDetailsBottomWrapper>


  );


}
