import React, { useState } from 'react';
import floating from 'floating.js';

import axios from 'axios';
import {
  ReactionButtonWrapper,
  ReactionsWrapper,
  EventIDWrapper,
  HostNameWrapper,
  HostImageWrapper,

  EventDetailsBottomWrapper,
  EventInfoWrapper,
  EventButtonsWrapper} from './EventDetailsBottom.styles';
import { useAppState } from "../../AppStateContext"
import {useAnimation, useMotionValue} from 'framer-motion';

import type { } from "styled-components/cssprop";
import { couldStartTrivia, createObjectBindingPattern, setSyntheticTrailingComments } from 'typescript';

interface EventDetailsBottomProps {

}

export const EventDetailsBottom: React.SFC<EventDetailsBottomProps> = () => {

  const { state } = useAppState();

  const [laugh, setLaugh] = useState(0);
  const [thumbUp, setThumbUp] = useState(0);
  const [love, setLove] = useState(0);
  const [mad, setMad] = useState(0);
  const [sad, setSad] = useState(0);
  const [opacity, setOpacity] = useState(100);
  const [beginning, setBeginning] = useState(0);
  const [sticker, setSticker] = useState<any>([]);
  const [transparent, setTransparent] = useState(100);
  const [full, setFull] = useState(0)
  const [full2, setFull2] = useState(0)
  const [full3, setFull3] = useState(0)
  const [full4, setFull4] = useState(0)
  const [full5, setFull5] = useState(0)
  const controls = useAnimation();
  const x = useMotionValue(0);

  const container = {
    hidden: { opacity : 0 },
    show: {
      opacity: 1
    }
  }

  const config = {
    headers: {
      'Content-Type': 'application/json',
      'Accept': 'application/json',
      'Access-Control-Allow-Origin': '*',
      'apiKey': 'c61fc5d0a6b7600ccb94edbca4e86a8a'
    }
  }
  React.useEffect(() => {
    setTransparent(100);
    fetch('/v1/package?userId=123&pageNumber=1&country=us', {
      // const response = fetch('/v1/package?userId=123&pageNumber=1&country=us', {
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'Access-Control-Allow-Origin': '*',
          'apiKey': 'c61fc5d0a6b7600ccb94edbca4e86a8a'
        },
        method:"get"
      })
      .then((response) => response.json())
      .then((json) => {setSticker(json.body.packageList)})      // .then((json) => setStickers(json.body.packageList))
      // .then(json => console.log(json))
        // .then((responseJson) => {console.log(responseJson)});
      // console.log(response);
  }, [])
  const between = (min, max) => Math.floor(
      Math.random() * (max - min) + min
    )

  return (

    <EventDetailsBottomWrapper>
      <div style={{ display: 'flex'}}>
        <HostImageWrapper src={state.eventAvatar} />

        <EventInfoWrapper>
          <HostNameWrapper>{state.eventName}</HostNameWrapper>
          <EventIDWrapper>Event  ID: {state.eventId}</EventIDWrapper>
        </EventInfoWrapper>
      </div>
      <div>
      <EventButtonsWrapper>

        <ReactionsWrapper>
          <ReactionButtonWrapper onClick={() => {
            floating({
              content: '<img src="https://img.stipop.io/2021/3/11/1615393182701_ByRaNm3vxi.gif" />',
              // content: "ðŸ™‹",
              number: 1,
              // direction: 'reverse',
              size: [1,10]
            });
            setTransparent(100);
            const wayUp = between(-270, -280);
            const isFull = between(1, 10);
            setFull(isFull);
            setLove(wayUp);
          }} src={sticker.length > 0 && sticker[0].packageImg} />

          <ReactionButtonWrapper onClick={() => {
            floating({
              content: '<img src="https://img.stipop.io/2021/3/10/1615387332944_6mixifGcUS.png" />',
              // content: "ðŸ™‹",
              number: 1,
              // direction: 'reverse',
              size: [1,10]
            });
            const wayUp = between(-280, -260);
            const isFull = between(1, 10);
            setFull2(isFull);
            setLaugh(wayUp);
          }} src={sticker.length > 0 && sticker[1].packageImg} />

          <ReactionButtonWrapper onClick={() => {
            floating({
              content: '<img src="https://img.stipop.io/2021/3/10/1615379395254_yricRq7WQI.png" />',
              // content: "ðŸ™‹",
              number: 1,
              // direction: 'reverse',
              size: [1,10]
            });
            const wayUp = between(-280, -260);
            const isFull = between(1, 10);

            setFull3(isFull);
            setThumbUp(wayUp);
          }} src={sticker.length > 0 && sticker[2].packageImg} />

          <ReactionButtonWrapper onClick={() => {
            floating({
              content: '<img src="https://img.stipop.io/2021/3/10/1615369378339_SNCtCQNERT.gif" />',
              // content: "ðŸ™‹",
              number: 1,
              // direction: 'reverse',
              size: [1,10]
            });
            const wayUp = between(-280, -260);
            const isFull = between(1, 10);

            setFull4(isFull);
            setMad(wayUp);
          }} src={sticker.length > 0 && sticker[3].packageImg} />

          <ReactionButtonWrapper  onClick={() => {
            floating({
              content: '<img src="https://img.stipop.io/2021/3/10/1615364413991_1afxMIWNhA.png" />',
              // content: "ðŸ™‹",
              number: 1,
              // direction: 'reverse',
              size: [1,10]
            });
            const wayUp = between(-280, -260);
            const isFull = between(1, 10);

            setFull5(isFull);
            setSad(wayUp);
          }} src={sticker.length > 0 && sticker[4].packageImg} />

          {/* <ReactionButtonWrapper animate={{ y: [thumbUp, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setThumbUp(wayUp);
          }} src={sticker[1].packageImg} />

          <ReactionButtonWrapper animate={{ y: [mad, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setMad(wayUp);
          }} src={sticker[2].packageImg} />

          <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setSad(wayUp)
          }} src={sticker[3].packageImg} />

          <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
            const wayUp = between(-280, -260);
            setSad(wayUp)
          }} src={sticker[4].packageImg} /> */}

        </ReactionsWrapper>



      </EventButtonsWrapper>
      <EventButtonsWrapper>

<ReactionsWrapper>
  <ReactionButtonWrapper 
    style={{x}} 
    animate={{y: [0, love], opacity: [full, 0]}}
    transition={{ ease: "easeOut", duration: 4}}  
    src={sticker.length > 0 && sticker[0].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, laugh], opacity: [full2, 0] }} 
    transition={{ ease: "easeOut", duration: 4 }}  

    src={sticker.length > 0 && sticker[1].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, thumbUp], opacity: [full3, 0] }}
    transition={{ ease: "easeOut", duration: 2 }}  

    src={sticker.length > 0 && sticker[2].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, mad], opacity: [full4, 0]}} 
    transition={{ ease: "easeOut", duration: 2 }}  

    src={sticker.length > 0 && sticker[3].packageImg} 
  />

  <ReactionButtonWrapper 
    animate={{ y: [0, sad], opacity: [full5, 0] }}
    transition={{ ease: "easeOut", duration: 2 }}  

    src={sticker.length > 0 && sticker[4].packageImg} 
  />

  {/* <ReactionButtonWrapper animate={{ y: [thumbUp, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setThumbUp(wayUp);
  }} src={sticker[1].packageImg} />

  <ReactionButtonWrapper animate={{ y: [mad, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setMad(wayUp);
  }} src={sticker[2].packageImg} />

  <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setSad(wayUp)
  }} src={sticker[3].packageImg} />

  <ReactionButtonWrapper animate={{ y: [sad, 0] }} onClick={() => {
    const wayUp = between(-280, -260);
    setSad(wayUp)
  }} src={sticker[4].packageImg} /> */}

</ReactionsWrapper>



</EventButtonsWrapper>
      </div>
    </EventDetailsBottomWrapper>


  );


}
